function Scene() {
  const controlsRef = useRef();
  const [zoomLevel, setZoomLevel] = useState(0);
  const zoomSpeed = 0.1; // Adjust zoom speed

  // Smooth zoom effect
  useFrame(() => {
    if (controlsRef.current) {
      const targetZoom = Math.max(controlsRef.current.minZoom, Math.min(controlsRef.current.maxZoom, controlsRef.current.zoom - zoomLevel));
      controlsRef.current.zoom += (targetZoom - controlsRef.current.zoom) * zoomSpeed;
      controlsRef.current.update();
    }
  });

  // Handle keyboard events for zoom
  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === 'w') {
        setZoomLevel((prev) => prev + 0.1); // Zoom in
      } else if (event.key === 's') {
        setZoomLevel((prev) => prev - 0.1); // Zoom out
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  return (
    <Canvas>
      <ambientLight intensity={0.1} />
      <directionalLight position={[0, 0, 5]} />
      <Model />
      <OrbitControls ref={controlsRef} enableDamping dampingFactor={0.1} />
    </Canvas>
  );
}




