function ControlledObject(props) {
  // This component represents an object in the scene with both mouse and keyboard control.
  const meshRef = useRef();
  const { camera, mouse } = useThree();
  const [isDragging, setIsDragging] = useState(false);

  // Keyboard movement speed
  const speed = 0.1;

  useEffect(() => {
    const handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          camera.position.z -= speed;
          break;
        case 'ArrowDown':
          camera.position.z += speed;
          break;
        case 'ArrowLeft':
          camera.position.x -= speed;
          break;
        case 'ArrowRight':
          camera.position.x += speed;
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [camera]);

  // Mouse drag effect
  useFrame(() => {
    if (isDragging) {
      meshRef.current.position.x += (mouse.x * 10 - meshRef.current.position.x) * 0.1;
      meshRef.current.position.y += (-mouse.y * 10 - meshRef.current.position.y) * 0.1;
    }
  });

  return (
    <mesh
      ref={meshRef}
      {...props}
      onPointerDown={(e) => setIsDragging(true)}
      onPointerUp={(e) => setIsDragging(false)}
      onPointerOut={(e) => setIsDragging(false)}>
      <boxGeometry args={[1, 1, 1]} />
      <meshStandardMaterial color="red" />
    </mesh>
  );
}

function Scene() {
  return (
    <Canvas>
      <ambientLight intensity={0.1} />
      <directionalLight position={[0, 0, 5]} />
      <ControlledObject position={new Vector3(0, 0, 0)} />
      <OrbitControls enablePan={true} enableZoom={true} enableRotate={true} />
    </Canvas>
  );
}

